import React from "react";
import Modal from "@material-ui/core/Modal";

import "./styles.css";
import { useEffect, useState } from "react";

function getModalStyle() {
    const top = 50;
    const left = 50;

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

function App() {
    const [modalStyle] = useState(getModalStyle);
    const [open, setOpen] = useState(false);
    const [users, setUsers] = useState(false);

    // REGISTER
    const [firstnameReg, setFirstNameReg] = useState('');
    const [lastnameReg, setLastNameReg] = useState('');
    const [passwordReg, setPasswordReg] = useState('');
    const [mailReg, setMailReg] = useState('');

    useEffect(() => {
        getUsers();
        console.log(users);
      }, [users]);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    function getUsers() {
        fetch('http://localhost:3001')
          .then(response => {
            return response.text();
          })
          .then(data => {
            setUsers(data);
          });
      }

      function UserRegister() {
        // let firstname = prompt('Enter merchant firstname');
        // let lastname = prompt('Enter merchant lastname');
        // let mail = prompt('Enter merchant email');
        // let password = prompt('Enter merchant password');

        fetch('http://localhost:3001/register', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({firstname:firstnameReg, lastname:lastnameReg, mail:mailReg, password:passwordReg}),
        })
          .then(response => {
            console.log(response);
        })
      };

    const register_form = (
        <div style={modalStyle} className="modal_open">
            <h2>Inscription</h2>
            <div>
                <form>
                    <div className="form_style">
                        <input
                            type="text"
                            id="ffirstname"
                            name="firtsname"
                            placeholder="Prénom"
                            onChange={(e) => {setFirstNameReg(e.target.value)}}
                        />
                        <input
                            type="text"
                            id="flastname"
                            name="lastname"
                            placeholder="Nom"
                            onChange={(e) => {setLastNameReg(e.target.value)}}
                        />
                        <input
                            type="text"
                            id="fmail"
                            name="mail"
                            placeholder="Adresse mail"
                            onChange={(e) => {setMailReg(e.target.value)}}
                        />
                        <input
                            type="password"
                            id="fpassword"
                            name="password"
                            placeholder="Mot de passe"
                            onChange={(e) => {setPasswordReg(e.target.value)}}
                        />
                        <input onClick={register} type="submit" value="S'INSCRIRE" />
                    </div>
                </form>
            </div>
        </div>
    );

    return (
        <div className="App">
            <div className="row no-gutters">
                {/* LEFT SIDE OF HOME PAGE */}
                <div
                    className="col no-gutters"
                    style={{
                        backgroundImage: `url("wallpaper.jpg")`,
                        backgroundSize: "cover",
                    }}
                >
                    <div className="leftside-title">
                        <p className="title">Bonjour à tous</p>
                        <p className="description">Bienvenue sur Lamster !</p>
                    </div>
                    <div className="social-link-footer">
                        <img
                            className="social-link-icon "
                            src="twitter.svg"
                            alt="twittericon"
                        />
                        <img
                            className="social-link-icon "
                            src="facebook.svg"
                            alt="facebookicon"
                        />
                        <img
                            className="social-link-icon "
                            src="instagram.svg"
                            alt="instagramicon"
                        />
                    </div>
                </div>

                {/* RIGHT SIDE OF HOME PAGE */}
                <div className="col no-gutters">
                    <div className="rightside">
                        <div
                            id="register"
                            onClick={handleOpen}
                            style={{
                                cursor: "pointer",
                                color: "white",
                                margin: "15px",
                                float: "right",
                            }}
                        >
                            S'inscrire
                        </div>
                        <Modal
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                        >
                            {register_form}
                        </Modal>
                        <div className="form">
                            <div>
                                <img
                                    className="brand_logo"
                                    src="brand_name.svg"
                                    alt="brand_name"
                                />
                            </div>
                            <div
                                style={{
                                    fontSize: "14px",
                                    marginTop: "10px",
                                    textAlign: "center",
                                }}
                            >
                                Nous sommes très heureux de vous revoir!
                            </div>
                            <div>
                                <form>
                                    <div className="form_style">
                                        <input
                                            type="text"
                                            id="fmail"
                                            name="mail"
                                            placeholder="Adresse mail"
                                        />
                                        <input
                                            type="password"
                                            id="fpassword"
                                            name="password"
                                            placeholder="Mot de passe"
                                        />
                                        <div style={{ float: "right" }}>
                                            Mot de passe oublié ?
                                        </div>
                                        <input
                                            // onClick={createUsers}
                                            type="submit"
                                            value="SE CONNECTER"
                                        />
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
