import React, { useEffect, useState, useRef } from "react";

function Popin({
    title,
    opened,
    close,
    closeOnOverlay,
    condensed,
    fullscreen,
    width,
    children,
}) {
    const popinRef = useRef();

    const [isVisible, setIsVisible] = useState(opened);

    useEffect(() => {
        setIsVisible(opened);
    }, [opened]);

    function handleClose() {
        close();
        setIsVisible(false);
    }
    function handleCloseOverlay() {
        if (closeOnOverlay) {
            close();
            setIsVisible(false);
        }
    }

    if (isVisible) {
        return (
            <div className="popin">
                <div className="popin__wrap" ref={popinRef}>
                    <div
                        className="popin__overlay"
                        onClick={handleCloseOverlay}
                    ></div>
                    <div
                        className="popin__content"
                        style={{ width: width, maxWidth: "auto" }}
                    >
                        {title != undefined && (
                            <div className="content__header">
                                <span className="header__title">{title}</span>
                            </div>
                        )}
                        <div
                            className={`content__body ${
                                condensed ? "condensed" : ""
                            } ${fullscreen ? "fullscreen" : ""}`}
                        >
                            {children}
                        </div>
                        <div className="content__close" onClick={handleClose}>
                            <i className="fas fa-times"></i>
                        </div>
                    </div>
                </div>
            </div>
        );
    } else {
        return null;
    }
}

export default Popin;
