import React from "react";
import Modal from "@material-ui/core/Modal";
import Alert from "@material-ui/lab/Alert";
import Snackbar from "@material-ui/core/Snackbar";
import Left_side from "./.js";

import "./styles.css";
import { useState } from "react";

function getModalStyle() {
    const top = 50;
    const left = 50;

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

function App() {
    const [modalStyle] = useState(getModalStyle);
    const [open, setOpen] = useState(false);
    const [forgetopen, setForgetOpen] = useState(false);
    const [snackbar, setSnackbar] = useState(false);

    //LOGIN
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    //REGISTER
    const [firstnameReg, setFirstNameReg] = useState("");
    const [lastnameReg, setLastNameReg] = useState("");
    const [passwordReg, setPasswordReg] = useState("");
    const [mailReg, setMailReg] = useState("");

    //FORGET PASSWORD ASK MAIL
    const [mailforget, setMailForget] = useState("");

    //LOGIN/PASSWORD RETOUR CLIENT
    const [loginStatus, setLoginStatus] = useState();
    const [registerStatus, setRegisterStatus] = useState();
    const [forgetStatus, setforgetStatus] = useState();

    const handleForgetClose = () => {
        setForgetOpen(false);
    };

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleCloseSnackbar = () => {
        setSnackbar(false);
    };

    function openForgetPassword() {
        setForgetOpen(true);
    }

    function UserRegister() {
        fetch("http://localhost:3001/register", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                firstname: firstnameReg,
                lastname: lastnameReg,
                mail: mailReg,
                password: passwordReg,
            }),
        }).then((response) => {
            if (response.status === 500) {
                setRegisterStatus(false);
            } else if (response.status === 200) {
                setRegisterStatus(true);
                setSnackbar(true);
            }
        });
        handleClose();
    }

    function ForgetPassword() {
        fetch("http://localhost:3001/forget", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                mail: mailforget,
            }),
        }).then((response) => {
            if (response.status === 500) {
                setPasswordReg(false);
            } else if (response.status === 200) {
                setPasswordReg(true);
                setSnackbar(true);
            }
        });
        handleClose();
    }

    function UserLogin() {
        fetch("http://localhost:3001/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                mail: username,
                password: password,
            }),
        }).then((response) => {
            response.json().then((json) => {
                if (json.auth) {
                    setLoginStatus("Connecté");
                } else {
                    setLoginStatus("Mail ou mot de passe incorrect");
                }
            });
        });
    }

    const register_form = (
        <div style={modalStyle} className="modal_open">
            <h2>Inscription</h2>
            <div>
                <div className="form_style">
                    <input
                        type="text"
                        placeholder="Prénom"
                        onChange={(e) => {
                            setFirstNameReg(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Nom"
                        onChange={(e) => {
                            setLastNameReg(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Adresse mail"
                        onChange={(e) => {
                            setMailReg(e.target.value);
                        }}
                    />
                    <input
                        type="password"
                        placeholder="Mot de passe"
                        onChange={(e) => {
                            setPasswordReg(e.target.value);
                        }}
                    />
                    {firstnameReg && lastnameReg && mailReg && passwordReg ? (
                        <input
                            onClick={() => {
                                UserRegister();
                            }}
                            type="submit"
                            placeholder="S'INSCRIRE"
                        />
                    ) : (
                        <input className="noaccess" defaultValue="S'INSCRIRE" />
                    )}
                </div>
            </div>
        </div>
    );

    const forgetpassword = (
        <div style={modalStyle} className="modal_open">
            <h2>Mot de passe oublié</h2>
            <div>
                <div className="form_style">
                    <input
                        type="text"
                        placeholder="Votre E-Mail"
                        onChange={(e) => {
                            setMailForget(e.target.value);
                        }}
                    />
                    {mailforget ? (
                        <input
                            onClick={() => {
                                ForgetPassword();
                            }}
                            type="submit"
                            placeholder="Envoyer"
                        />
                    ) : (
                        <input className="noaccess" defaultValue="Envoyer" />
                    )}
                </div>
            </div>
        </div>
    );

    return (
        <div className="App">
            <div className="row no-gutters">
                {/* LEFT SIDE OF HOME PAGE */}
                <left_side></left_side>
                {/* RIGHT SIDE OF HOME PAGE */}
                <div className="col no-gutters">
                    <div className="rightside">
                        <div
                            id="register"
                            onClick={handleOpen}
                            style={{
                                cursor: "pointer",
                                color: "white",
                                margin: "15px",
                                float: "right",
                            }}
                        >
                            S'inscrire
                        </div>

                        {/* MODAL MOT DE PASSE OUBLIÉ */}
                        <Modal
                            open={forgetopen}
                            onClose={handleForgetClose}
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                        >
                            {forgetpassword}
                        </Modal>

                        {/* MODAL S'INSCRIRE */}
                        <Modal
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                        >
                            {register_form}
                        </Modal>

                        <div className="form">
                            <div>
                                <img
                                    className="brand_logo"
                                    src="brand_name.svg"
                                    alt="brand_name"
                                />
                            </div>
                            <div
                                style={{
                                    fontSize: "14px",
                                    marginTop: "10px",
                                    textAlign: "center",
                                }}
                            >
                                Nous sommes très heureux de vous revoir!
                            </div>
                            <div>
                                <div className="form_style">
                                    <input
                                        type="text"
                                        placeholder="Adresse mail"
                                        onChange={(e) => {
                                            setUsername(e.target.value);
                                        }}
                                    />
                                    <input
                                        type="password"
                                        placeholder="Mot de passe"
                                        onChange={(e) => {
                                            setPassword(e.target.value);
                                        }}
                                    />
                                    <div
                                        onClick={openForgetPassword}
                                        style={{
                                            float: "right",
                                            color: "grey",
                                            cursor: "pointer",
                                        }}
                                    >
                                        Mot de passe oublié ?
                                    </div>
                                    {password && username ? (
                                        <input
                                            type="submit"
                                            onClick={UserLogin}
                                        />
                                    ) : (
                                        <input
                                            defaultValue="Envoyer"
                                            className={"noaccess"}
                                        />
                                    )}
                                </div>
                                {loginStatus && (
                                    <h1
                                        style={{
                                            textAlign: "center",
                                            color: "red",
                                            fontSize: "12px",
                                            marginTop: "15px",
                                        }}
                                    >
                                        {loginStatus}
                                    </h1>
                                )}
                                {registerStatus && (
                                    <div>
                                        <Snackbar
                                            open={snackbar}
                                            autoHideDuration={4000}
                                            onClose={handleCloseSnackbar}
                                            anchorOrigin={{
                                                vertical: "top",
                                                horizontal: "center",
                                            }}
                                        >
                                            <Alert
                                                onClose={handleCloseSnackbar}
                                                severity="success"
                                            >
                                                Compte crée avec succès !
                                            </Alert>
                                        </Snackbar>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
