import React from "react";
import Modal from "@material-ui/core/Modal";

import "./styles.css";
import { useState } from "react";

function getModalStyle() {
    const top = 50;
    const left = 50;

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

function App() {
    const [modalStyle] = useState(getModalStyle);
    const [open, setOpen] = useState(false);

    //LOGIN
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    // REGISTER
    const [firstnameReg, setFirstNameReg] = useState("");
    const [lastnameReg, setLastNameReg] = useState("");
    const [passwordReg, setPasswordReg] = useState("");
    const [mailReg, setMailReg] = useState("");

    const [loginStatus, setLoginStatus] = useState("");

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    function UserRegister() {
        fetch("http://localhost:3001/register", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                firstname: firstnameReg,
                lastname: lastnameReg,
                mail: mailReg,
                password: passwordReg,
            }),
        }).then((response) => {
            console.log("????");
            console.log(response);
        });
        handleClose();
    }

    function UserLogin() {
        fetch("http://localhost:3001/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                mail: username,
                password: password,
            }),
        }).then((response) => {
            if (response.status === 401) {
                setLoginStatus("Erreur de login");
            } else {
                setLoginStatus("ConnectÃ© !");
            }
        });
    }

    const register_form = (
        <div style={modalStyle} className="modal_open">
            <h2>Inscription</h2>
            <div>
                <div className="form_style">
                    <input
                        type="text"
                        placeholder="PrÃ©nom"
                        onChange={(e) => {
                            setFirstNameReg(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Nom"
                        onChange={(e) => {
                            setLastNameReg(e.target.value);
                        }}
                    />
                    <input
                        type="text"
                        placeholder="Adresse mail"
                        onChange={(e) => {
                            setMailReg(e.target.value);
                        }}
                    />
                    <input
                        type="password"
                        placeholder="Mot de passe"
                        onChange={(e) => {
                            setPasswordReg(e.target.value);
                        }}
                    />

                    <input
                        onClick={() => {
                            UserRegister();
                        }}
                        type="submit"
                        placeholder="S'INSCRIRE"
                    />
                </div>
            </div>
        </div>
    );

    return (
        <div className="App">
            <div className="row no-gutters">
                {/* LEFT SIDE OF HOME PAGE */}
                <div
                    className="col no-gutters"
                    style={{
                        backgroundImage: `url("wallpaper.jpg")`,
                        backgroundSize: "cover",
                    }}
                >
                    <div className="leftside-title">
                        <p className="title">Bonjour Ã  tous</p>
                        <p className="description">Bienvenue sur Lamster !</p>
                    </div>
                    <div className="social-link-footer">
                        <img
                            className="social-link-icon "
                            src="twitter.svg"
                            alt="twittericon"
                        />
                        <img
                            className="social-link-icon "
                            src="facebook.svg"
                            alt="facebookicon"
                        />
                        <img
                            className="social-link-icon "
                            src="instagram.svg"
                            alt="instagramicon"
                        />
                    </div>
                </div>

                {/* RIGHT SIDE OF HOME PAGE */}
                <div className="col no-gutters">
                    <div className="rightside">
                        <div
                            id="register"
                            onClick={handleOpen}
                            style={{
                                cursor: "pointer",
                                color: "white",
                                margin: "15px",
                                float: "right",
                            }}
                        >
                            S'inscrire
                        </div>
                        <Modal
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                        >
                            {register_form}
                        </Modal>
                        <div className="form">
                            <div>
                                <img
                                    className="brand_logo"
                                    src="brand_name.svg"
                                    alt="brand_name"
                                />
                            </div>
                            <div
                                style={{
                                    fontSize: "14px",
                                    marginTop: "10px",
                                    textAlign: "center",
                                }}
                            >
                                Nous sommes trÃ¨s heureux de vous revoir!
                            </div>
                            <div>
                                <div className="form_style">
                                    <input
                                        type="text"
                                        placeholder="Adresse mail"
                                        onChange={(e) => {
                                            setUsername(e.target.value);
                                        }}
                                    />
                                    <input
                                        type="password"
                                        placeholder="Mot de passe"
                                        onChange={(e) => {
                                            setPassword(e.target.value);
                                        }}
                                    />
                                    <div style={{ float: "right" }}>
                                        Mot de passe oubliÃ© ?
                                    </div>
                                    <input type="submit" onClick={UserLogin} />
                                </div>
                                <h1>{loginStatus}</h1>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
