import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Modal from "@material-ui/core/Modal";

import "./styles.css";

function rand() {
    return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const useStyles = makeStyles((theme) => ({
    paper: {
        position: "absolute",
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: "2px solid #000",
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

function App() {
    const classes = useStyles();
    // getModalStyle is not a pure function, we roll the style only on the first render
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const register = (
        <div style={modalStyle} className="modal">
            <h2 id="simple-modal-title">Text in a modal</h2>
            <p id="simple-modal-description">
                Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
            </p>
        </div>
    );

    return (
        <div className="App">
            <div className="row no-gutters">
                {/* LEFT SIDE OF HOME PAGE */}
                <div
                    className="col no-gutters"
                    style={{
                        backgroundImage: `url("wallpaper.jpg")`,
                        backgroundSize: "cover",
                    }}
                >
                    <div className="leftside-title">
                        <p className="title">Bonjour à tous</p>
                        <p className="description">Bienvenue sur Lamster !</p>
                    </div>
                    <div className="social-link-footer">
                        <img
                            className="social-link-icon "
                            src="twitter.svg"
                            alt="twittericon"
                        />
                        <img
                            className="social-link-icon "
                            src="facebook.svg"
                            alt="facebookicon"
                        />
                        <img
                            className="social-link-icon "
                            src="instagram.svg"
                            alt="instagramicon"
                        />
                    </div>
                </div>

                {/* RIGHT SIDE OF HOME PAGE */}
                <div className="col no-gutters">
                    <div className="rightside">
                        <div
                            id="register"
                            onClick={handleOpen}
                            style={{
                                cursor: "pointer",
                                color: "white",
                                margin: "15px",
                                float: "right",
                            }}
                        >
                            S'inscrire
                        </div>
                        <Modal
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                        >
                            {register}
                        </Modal>
                        <div className="form">
                            <div>
                                <img
                                    className="brand_logo"
                                    src="brand_name.svg"
                                    alt="brand_name"
                                />
                            </div>
                            <div
                                style={{
                                    fontSize: "14px",
                                    marginTop: "10px",
                                    textAlign: "center",
                                }}
                            >
                                Nous sommes très heureux de vous revoir!
                            </div>
                            <div>
                                <form>
                                    <div className="form_style">
                                        <input
                                            type="text"
                                            id="fmail"
                                            name="mail"
                                            placeholder="Adresse mail"
                                        />
                                        <input
                                            type="password"
                                            id="fpassword"
                                            name="password"
                                            placeholder="Mot de passe"
                                        />
                                        <div style={{ float: "right" }}>
                                            Mot de passe oublié ?
                                        </div>
                                        <input
                                            type="submit"
                                            value="SE CONNECTER"
                                        />
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
