import React from "react";
import "./styles.css";

function App() {
    function openModal() {
        console.log("ccccc");
        // // Get the modal
        // var modal = document.getElementById("myModal");

        // // Get the button that opens the modal
        // var btn = document.getElementById("myBtn");

        // // Get the <span> element that closes the modal
        // var span = document.getElementsByClassName("close")[0];

        // // When the user clicks the button, open the modal
        // btn.onclick = function () {
        //     modal.style.display = "block";
        // };

        // // When the user clicks on <span> (x), close the modal
        // span.onclick = function () {
        //     modal.style.display = "none";
        // };

        // // When the user clicks anywhere outside of the modal, close it
        // window.onclick = function (event) {
        //     if (event.target == modal) {
        //         modal.style.display = "none";
        //     }
        // };
    }

    return (
        <div className="App">
            <div className="row no-gutters">
                {/* LEFT SIDE OF HOME PAGE */}
                <div
                    className="col no-gutters"
                    style={{
                        backgroundImage: `url("wallpaper.jpg")`,
                        backgroundSize: "cover",
                    }}
                >
                    <div className="leftside-title">
                        <p className="title">Bonjour à tous</p>
                        <p className="description">Bienvenue sur Lamster !</p>
                    </div>
                    <div className="social-link-footer">
                        <img
                            className="social-link-icon "
                            src="twitter.svg"
                            alt="twittericon"
                        />
                        <img
                            className="social-link-icon "
                            src="facebook.svg"
                            alt="facebookicon"
                        />
                        <img
                            className="social-link-icon "
                            src="instagram.svg"
                            alt="instagramicon"
                        />
                    </div>
                </div>

                {/* RIGHT SIDE OF HOME PAGE */}
                <div className="col no-gutters">
                    <div className="rightside">
                        <div
                            onClick={() => openModal()}
                            style={{
                                color: "white",
                                margin: "15px",
                                float: "right",
                            }}
                        >
                            S'inscrire
                        </div>
                        <div className="form">
                            <div>
                                <img
                                    className="brand_logo"
                                    src="brand_name.svg"
                                    alt="brand_name"
                                />
                            </div>
                            <div
                                style={{
                                    fontSize: "14px",
                                    marginTop: "10px",
                                    textAlign: "center",
                                }}
                            >
                                Nous sommes très heureux de vous revoir!
                            </div>
                            <div>
                                <form>
                                    <div className="form_style">
                                        <input
                                            type="text"
                                            id="fmail"
                                            name="mail"
                                            placeholder="Adresse mail"
                                        />
                                        <input
                                            type="password"
                                            id="fpassword"
                                            name="password"
                                            placeholder="Mot de passe"
                                        />
                                        <a style={{ float: "right" }}>
                                            Mot de passe oublié ?
                                        </a>
                                        <input
                                            type="submit"
                                            value="SE CONNECTER"
                                        />
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
